import axios from "axios";

const actions = {
    // Responsável por alterar os dados de um produto
    // updateCard({ commit }, obj) {
    //     // Processa a api
    //     const endpoint = `${this.state.apiURL}card/${obj.id}/edit`;

    //     return fetch(endpoint, {
    //         method: "POST",
    //         body: obj
    //     })
    //         .then(function (response) {
    //             if (response.ok) {
    //                 commit('');
    //                 return true;
    //             }
    //         })
    //         .catch(function (error) {
    //             // eslint-disable-next-line no-undef
    //             Toast.fire(error.message, "", "error");
    //         })
    // }

    updateCard({ commit }, obj) {
        // Processa a api
        const endpoint = `card/${obj.id}/edit`;

        return axios.post(endpoint, obj)
            .then(res => {
                if (res.ok) {
                    commit('');
                    return true;
                }
            })
            .catch(error => {
                console.log(error);
            });
    }
}

export default actions;
